import com.google.gson.JsonObjectimport io.reactivex.Observableimport org.json.JSONObjectimport retrofit2.http.Fieldimport retrofit2.http.FormUrlEncodedimport retrofit2.http.POSTimport java.util.*interface BookingService {    //login    @FormUrlEncoded    @POST("customer/login")    fun login(        @Field("cust_email") cust_email: String,        @Field("cust_mobile") cust_mobile: String    ): Observable<JsonObject>    //Customer Login    @FormUrlEncoded    @POST("customer/create")    fun register(        @Field("cust_firstname") cust_firstname: String,        @Field("cust_lastname") cust_lastname: String,        @Field("cust_email") cust_email: String,        @Field("cust_mobile") cust_mobile: String    ): Observable<JsonObject>    //Create Booking    @FormUrlEncoded    @POST("customer/Booking")    fun createBooking(        @Field("r_capacity") r_capacity: Int,        @Field("r_type") r_type: String,        @Field("r_startdate") r_startdate: String,        @Field("r_enddate") r_enddate: String    ): Observable<JsonObject>    @FormUrlEncoded    @POST("room/create")    fun createRoom(        @Field("r_capacity") r_capacity: Int,        @Field("r_type") r_type: String    ): Observable<JsonObject>}